import { useEffect, useState } from 'react';
import Header from './components/Header';
import axios from 'axios';

function App() {

  const [page,setPage] = useState(1);
  const [sort,setSort] =useState('popularity.desc');
  const [totalPage,setTotalPage] = useState(0);
  const [movieList,setMovieList] = useState([]);
  const IMG_API = 'https://image.tmdb.org/t/p/w1200';
  useEffect(()=>{
    axios.get(`https://api.themoviedb.org/3/discover/movie?sort_by=${sort}&api_key=04c35731a5ee918f014970082a0088b1&page=${page}`)
    .then(res => {
    const data = res.data;
    setMovieList(data.results);
    setTotalPage(data.total_pages);
      console.log(data);
    })
  },[])

  return (
    <div className="App">
      <Header/>

      {totalPage}
      { movieList ?  movieList.map((movie,key)=>{
        return <div key={key} ><img src={IMG_API + movie.poster_path}/></div>
      }) : ''}
    </div>
  );
}

export default App;
