import { useEffect, useState } from 'react';
import Header from './components/Header';
import MovieList from './components/MovieList';
import axios from 'axios';

function App() {

  const [page,setPage] = useState(1);
  const [sort,setSort] =useState('popularity.desc');
  const [totalPage,setTotalPage] = useState(0);
  const [movieList,setMovieList] = useState([]);
  useEffect(()=>{
    fetchMovies();
  },[])
  function fetchMovies(){
    console.log(page);
    axios.get(`https://api.themoviedb.org/3/discover/movie?sort_by=${sort}&api_key=04c35731a5ee918f014970082a0088b1&page=${page}`)
    .then(res => {
    const data = res.data;
    setMovieList(data.results);
    setTotalPage(data.total_pages);
      console.log(data);
    })
  }
  return (
    <div className="App">
      <Header/>
      <MovieList data={movieList} />
      <div style={{display:'flex',width:'100%','justifyContent':'center',padding:'30px'}}>
        <button onClick={()=>{setPage(page);}}>{page}</button>
        <button onClick={()=>{
          setPage(page + 1);
          fetchMovies();
          }}>{page + 1}</button>
        <button onClick={()=>{setPage(page + 2);fetchMovies();}}>{page + 2}</button>
      </div>

    </div>
  );
}

export default App;
